mapper.update:
update account_detail
pin =#{pin}, proxy_id =#{proxyId}, test =#{test}, token_type =#{tokenType}, biz_type =#{bizType}, biz_token =#{bizToken}, biz_id =#{bizId}, is_robot =#{isRobot}, charge_type =#{chargeType}, before_amount =#{beforeAmount}, before_freeze =#{beforeFreeze}, change_amount =#{changeAmount}, change_freeze =#{changeFreeze}, after_amount =#{afterAmount}, after_freeze =#{afterFreeze}, status =#{status}, del_status =#{delStatus}, create_time =#{createTime}, update_time =#{updateTime},
where id=#{id}

mapper.select:
select count(1) from


      (
       SELECT DISTINCT(pin) FROM account_detail

           is_robot =#{isRobot}
           AND proxy_id =#{proxyId}
           AND biz_token =#{bizToken}
           AND to_days(create_time) = to_days(now())
           AND YEARWEEK(date_format(create_time,'%Y-%m-%d')) = YEARWEEK(now())
           AND DATE_FORMAT( create_time, '%Y%m' ) = DATE_FORMAT( CURDATE( ) , '%Y%m' )

        ) s


       account_detail

SELECT IFNULL(SUM(change_amount),0) FROM account_detail

    is_robot =#{isRobot}
    AND pin =#{pin},
    AND proxy_id =#{proxyId}
    AND biz_token =#{bizToken}
    AND to_days(create_time) = to_days(now())
    AND YEARWEEK(date_format(create_time,'%Y-%m-%d')) = YEARWEEK(now())
    AND DATE_FORMAT( create_time, '%Y%m' ) = DATE_FORMAT( CURDATE( ) , '%Y%m' )

choose.otherwise:
select @a:=b.cc-a.cc AS a FROM
(SELECT COUNT(1) as cc FROM (
SELECT DISTINCT(pin) FROM account_detail

    is_robot =#{isRobot}
    AND proxy_id =#{proxyId}
    AND biz_token =#{bizToken}
    AND to_days(create_time) < to_days(now())
    AND YEARWEEK(date_format(create_time,'%Y-%m-%d')) < YEARWEEK(now())
    AND DATE_FORMAT( create_time, '%Y%m' ) < DATE_FORMAT( CURDATE( ) , '%Y%m' )

) cdi ) a,
( SELECT COUNT(1) as cc FROM (
SELECT DISTINCT(pin) FROM account_detail

    is_robot =#{isRobot}
    AND proxy_id =#{proxyId}
    AND biz_token =#{bizToken}

) dis) b

